import React, {createContext, useEffect, useState, useContext, useRef} from 'react'
import { SocketContext } from "../SocketContext";

const VideoContext = createContext();

const Video = ({socket, username, room}) => {
  const { name, callAccepted, userVideo, callEnded, call, joinRoom, send_message, answerCall } = useContext(SocketContext);

  const [msgList, setmsgList] = useState([]);
  const [devices, setDevices] = useState([]);
  const [selectedDeviceId, setSelectedDeviceId] = useState(null);
  const [currMsg, setcurrMsg] = useState("");
  const [muteBtn, setMutebtn] = useState(false);
  const [cameraBtn, setCamerabtn] = useState(true);

  const myVideo = useRef(null);
  const select = useRef(null);


  async function getCameras() {
    try {
      const devices = await navigator.mediaDevices.enumerateDevices();
      const cameras = devices.filter((device) => device.kind === "videoinput");
      const currentCamera = myStream.getVideoTracks()[0];

    } catch (e) {
      console.log(e);
    }
  }

  useEffect(() => {
    async function getMedia(deviceId) {
      const initialConstrains = {
        audio: true,
        video: { facingMode: "user" },
      };
      const cameraConstraints = {
        audio: true,
        video: { deviceId: { exact: deviceId } },
      };
      try {

        const myStream = await navigator.mediaDevices.getUserMedia(
          deviceId ? cameraConstraints : initialConstrains
        );
        myVideo.current.srcObject = myStream;

        if (!deviceId) {
          await getCameras();
        }
      } catch (e) {
        console.log(e);
      }
    }
  getMedia();
  console.log("myStream", myStream);

  }, []);


  // useEffect(() => {
  //   navigator.mediaDevices.getUserMedia({ video: true, audio: false })
  //     .then((currentStream) => {
  //       setStream(currentStream);
  //       myVideo.current.srcObject = currentStream;
  //     });



  // }, []);

  function handleCameraClick () {
    setCamerabtn(!cameraBtn);
    myVideo.current.srcObject.getVideoTracks()[0].enabled = cameraBtn;
  }

  function handleMuteClick () {
    setMutebtn(!muteBtn);
    myVideo.current.srcObject.getAudioTracks()[0].enabled = !muteBtn;
  }

  return (
    <VideoContext.Provider value={{currMsg}}> 
        <div>
            <div className="chat-header">
                <p> Live Chat </p>
            </div>
            <button onClick={handleMuteClick}> {muteBtn? "Unmute" : "Mute"} </button>
            <button onClick={handleCameraClick}> {cameraBtn ? "Turn Video Off" : "Turn Video On"} </button>

            <video ref={myVideo} autoPlay />

            <select value={selectedDeviceId} onChange={getCameras}>
              <option value="">
                select a device 
              </option>
              {devices.map((device) => (
                <option key={device.deviceId} value={device.deviceId}>{device.label}</option>
              ))}
            </select>
            <video playsInline muted ref={userVideo} autoPlay />


        </div>

    </VideoContext.Provider>

  );
}

export { Video, VideoContext };
